import React, {Component, useEffect, useState} from 'react';
import './App.css';
import bg from "./bg.png";
import {HashRouter as Router, Route, Switch} from 'react-router-dom';
import Pokedex from "./Components/Card/Pokedex";
import SearchBar from "./Components/SearchBar/SearchBar";
import Dashboard from "./Components/Layout/Dashboard";


const App = () => {
    const handleFilters = typeFilters => {
        if (!typeFilters) {
            this.setState({ typeFilters: [] });
        }
        this.setState({ typeFilters: typeFilters });
    };
      return (
          <Router>
            <div>
            {/*<div style={{background:`url(${bg})`}}>*/}
                <div className="Header">
                    {/*<Nav/>*/}
                    <SearchBar filter={this.handleFilters} />
                </div>
              <div className="container">
                <Switch>
                    <Route
                        exact
                        path="/"
                        render={() => <Pokedex filterList={this.state.typeFilters} />}
                    />
                    <Route exact path="/" component={Dashboard} />
                    <Route exact path="/pokemon/:pokemonIndex" component={Pokedex} />
                </Switch>
              </div>
            </div>
          </Router>

      );
    }

export default App;
