import React, { Component } from "react";
import Pokemon from "./Components/Pokemon/Pokemon";
import SearchBar from "./Components/SearchBar/SearchBar";
import PokemonDetail from "./Components/PokemonDetail/PokemonDetail";
import { BrowserRouter, Route, Switch } from "react-router-dom";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            typeFilters: []
        };
    }

    handleFilters = typeFilters => {
        if (!typeFilters) {
            this.setState({ typeFilters: [] });
        }
        this.setState({ typeFilters: typeFilters });
    };

    render() {
        return (
            <BrowserRouter basename={process.env.PUBLIC_URL}>
                <div>
                    <SearchBar filter={this.handleFilters} />
                    <Switch>
                        <Route
                            exact
                            path="/"
                            render={() => <Pokemon filterList={this.state.typeFilters} />}
                        />
                        <Route exact path="/detail/:name" component={PokemonDetail} />
                    </Switch>
                </div>
            </BrowserRouter>
        );
    }
}

export default App;
