import React, {useEffect, useState} from 'react';
import logo from './logo.svg';
import './App.css';
import {getAllPokemon, getPokemon} from "./Services/pokemon";
import Card from "./Components/Card"
import Nav from "./Components/Nav/";
import bg from "./bg.png";
import {HashRouter as Router, Route, Switch} from 'react-router-dom';
import Pokedex from "./Components/Card/Pokedex";
import SearchBar from "./Components/SearchBar/SearchBar";
import Dashboard from "./Components/Layout/Dashboard";

function App() {
  //data get from pokemon api,initial state pass an array
  const [pokemonData, setPokemonData] = useState([]);
  //store the url of next page
  const [nextpageUrl, setNextPageurl] = useState('');
  const [prevpageUrl, setPrevPageurl] = useState('');
  //set data state when use api
  const [loading, setLoading] = useState(true);
  // string
  const currentUrl = 'https://pokeapi.co/api/v2/pokemon'

  //one function, one array
  useEffect(() =>{
    async function fetchData(){
      let response = await getAllPokemon(currentUrl);
      console.log(response)
      //response is the data get from api, it has previous and next to the other page
      setNextPageurl(response.next);
      setPrevPageurl(response.previous);
      //passing the results array
      let pokemon = await loadingPokemon(response.results);
      //get data back from Api
      setLoading(false);
    }
    //recreate the function
    fetchData();

  },[]);

  const next = async () => {
    setLoading(true);
    let data = await getAllPokemon(nextpageUrl);
    await loadingPokemon(data.results);
    setNextPageurl(data.next);
    setPrevPageurl(data.previous);
    setLoading(false);
  }

  const prev = async () => {
    if (!prevpageUrl) return;
    setLoading(true);
    let data = await getAllPokemon(prevpageUrl);
    await loadingPokemon(data.results);
    setNextPageurl(data.next);
    setPrevPageurl(data.previous);
    setLoading(false);
  }


  const loadingPokemon = async (data) => {
    //local pokemon array take in an array of promises return an array after all promises resolve
    let _pokemonData = await Promise.all(data.map(async pokemon => {
      let pokemonRecord = await getPokemon(pokemon.url);
      return pokemonRecord
    }))
    //store data into react state
    setPokemonData(_pokemonData)
  }

  return (
      //render to the screen
      // <Router>
      // <div>
      //   {loading ? <h1>...loading...</h1>:(
      //       <div style={{background:`url(${bg})`}}>
      //         <Nav/>
      //         <div className="btn">
      //           <button onClick={prev}>Prev</button>
      //           <button onClick={next}>Next</button>
      //         </div>
      //         <div className="card-grid-container">
      //           <Switch>
      //             {/*<Route exact path="/" component = {Card}/>*/}
      //             <Route exact path="/pokemon/:pokemonIndex" component = {Pokedex}/>
      //           </Switch>
      //           {pokemonData.map((pokemon, i) => {
      //             return <Card key ={i} pokemon = {pokemon}/>
      //           })
      //           }
      //         </div>
      //         <div className="btn">
      //           <button onClick={prev}>Prev</button>
      //           <button onClick={next}>Next</button>
      //         </div>
      //       </div>
      //   )}
      // </div>
      // </Router>
      <Router>
        <div style={{background:`url(${bg})`}}>
          <Nav/>
          <SearchBar/>
          <div className="container">
            <Switch>
              <Route exact path="/" component={Dashboard()} />
              <Route exact path="/pokemon/:pokemonIndex" component={Pokedex} />
            </Switch>
          </div>
        </div>
      </Router>

  );
}

export default App;
