import React, { Component } from 'react';
import { render } from 'react-dom';

const HTTP = {
    baseUrl: "http://pokeapi.co",
    get: function (uri) {
        return fetch(this.baseUrl + uri)
            .then(function (res) {
                return res.json();
            });
    }
};


const Actions = Reflux.createActions([
    "getPokemons",
    "sort",
    "search",
    "resetSearch",
    "getDetails"
]);


var PokemonStore = Reflux.createStore({
    listenables: [Actions],

    getPokemons: function (start, end) {
        if (!this.pokemons) this.pokemons = [];
        var check;
        for (var i = start; i < end; i++) {
            if (i > 718) return this.sort("Lowest Number First");
            (function (i) {
                HTTP.get("/api/v1/pokemon/" + i)
                    .then(function (data) {
                        check = this.checkDuplicates(data.pkdx_id);
                        if (check) {
                            var pokemon = {
                                name: data.name,
                                type: data.types,
                                id: data.pkdx_id,
                                imgUrl: "http://pokeapi.co/media/img/" + data.pkdx_id + ".png",
                                detailsUrl: "/pokemons/" + data.pkdx_id
                            };
                            this.pokemons.push(pokemon);
                        }

                        if (i === end - 1) this.sort("Lowest Number First");

                    }.bind(this));
            }.bind(this))(i);
        }
    },

    checkDuplicates: function (pokemonId) {
        return this.pokemons.every(function (item) {
            return item.id !== pokemonId;
        });
    },

    sort: function (method) {
        this.pokemons = this.pokemons.sort(function (a, b) {
            switch (method) {
                case "A-Z":
                    return a.name.localeCompare(b.name);

                case "Z-A":
                    return b.name.localeCompare(a.name);

                case "Lowest Number First":
                    return a.id - b.id;

                case "Highest Number First":
                    return b.id - a.id;
            }
        });

        this.fireUpdate();
    },

    search: function (searchQuery) {
        if (!this.backup) this.backup = this.pokemons;

        searchQuery = searchQuery.toLowerCase();

        this.pokemons = this.backup.filter(function (item) {
            if (item.name.toLowerCase().indexOf(searchQuery) > -1 || item.id.toString().indexOf(searchQuery) > -1) {
                return true;
            } else if (item.type[0].name.toLowerCase() === searchQuery) {
                return true;
            } else if (typeof item.type[1] !== "undefined" && item.type[1].name === searchQuery) {
                return true;
            }
        });

        this.fireUpdate();
    },

    resetSearch: function () {
        this.pokemons = this.backup;
        this.fireUpdate();
    },

    fireUpdate: function () {
        this.trigger("change", this.pokemons);
    }
});


var PokemonDetailStore = Reflux.createStore({
    listenables: [Actions],

    getDetails: function (pokemonId) {
        var pokemon = {};
        HTTP.get("/api/v1/pokemon/" + pokemonId)
            .then(function (data) {
                pokemon.name = data.name;
                pokemon.type = data.types;
                pokemon.id = data.pkdx_id;
                pokemon.imgUrl = "http://pokeapi.co/media/img/" + data.pkdx_id + ".png";
                pokemon.height = data.height;
                pokemon.weight = data.weight;
                pokemon.attack = data.attack;
                pokemon.defense = data.defense;
                pokemon.hp = data.hp;
                pokemon.sp_atk = data.sp_atk;
                pokemon.sp_def = data.sp_def;
                pokemon.speed = data.speed;

                // description
                HTTP.get(data.descriptions[0].resource_uri)
                    .then(function (data2) {
                        pokemon.description = data2.description;

                        // weaknesses
                        HTTP.get(data.types[0].resource_uri)
                            .then(function (data3) {
                                pokemon.weaknesses = data3.weakness;

                                // category
                                HTTP.get("/api/v2/pokemon-species/" + data.pkdx_id)
                                    .then(function (data4) {
                                        pokemon.category = data4.genera[0].genus;

                                        // get ability description
                                        HTTP.get("/api/v2/ability/" + data.abilities[0].name)
                                            .then(function (data) {
                                                pokemon.ability = data.names[0].name;
                                                pokemon.abilityDescription = data.flavor_text_entries[1].flavor_text;
                                                this.trigger("change", pokemon);
                                            }.bind(this));
                                    }.bind(this));
                            }.bind(this));
                    }.bind(this));
            }.bind(this));
    }
});


