var PokemonType = React.createClass({
    getInitialState: function () {
        return {
            backgroundColor: "",
            fontColor: "#FFF"
        };
    },

    componentWillMount: function () {
        this.getColor(this.props.pokemonType);
    },

    componentWillReceiveProps: function (nextProps) {
        this.getColor(nextProps.pokemonType);
    },

    uppercaser: function (str) {
        return str.replace(str[0], str[0].toUpperCase());
    },

    getColor: function (pokemonType) {
        var background = "";
        var font = "#FFF";

        switch (pokemonType) {
            case "poison":
                background = "#b97fc9";
                break;

            case "grass":
                background = "#9bcc50";
                font = "#333";
                break;

            case "ice":
                background = "#51c4e7";
                font = "#333";
                break;

            case "flying":
                background = "linear-gradient(180deg, #3dc7ef 50%, #bdb9b8 50%)";
                font = "#333";
                break;

            case "fire":
                background = "#fd7d24";
                break;

            case "psychic":
                background = "#f366b9";
                break;

            case "bug":
                background = "#729f3f";
                break;

            case "dragon":
                background = "linear-gradient(180deg, #53a4cf 50%, #f16e57 50%)";
                break;

            case "fairy":
                background = "#fdb9e9";
                font = "#333";
                break;

            case "ghost":
                background = "#7b62a3";
                break;

            case "ground":
                background = "linear-gradient(180deg, #f7de3f 50%, #ab9842 50%)";
                font = "#333";
                break;

            case "normal":
                background = "#a4acaf";
                break;

            case "steel":
                background = "#9eb7b8";
                font = "#333";
                break;

            case "dark":
                background = "#707070";
                break;

            case "electric":
                background = "#eed535";
                font = "#333";
                break;

            case "fighting":
                background = "#d56723";
                break;

            case "rock":
                background = "#a38c21";
                break;

            case "water":
                background = "#4592c4";
                break;
        }
        this.setState({ fontColor: font });
        this.setState({ backgroundColor: background });
    },

    render: function () {

        var colStyle = {
            textAlign: "center",
            background: this.state.backgroundColor,
            border: this.props.border
        };

        var typeStyle = {
            marginTop: 8,
            color: this.state.fontColor,
            fontWeight:700
        };

        return (
            <div style={colStyle} className={this.props.colSize}>
                <p style={typeStyle}>{this.uppercaser(this.props.pokemonType)}</p>
            </div>
        );
    }
});


