var HomePage = React.createClass({
    mixins: [Reflux.listenTo(PokemonStore, "onChange")],

    getInitialState: function () {
        return {
            pokemons: [],
            isLoading: false
        };
    },

    componentWillMount: function () {
        Actions.getPokemons(1, 13);

        $(window).scroll(function () {
            if ($(window).scrollTop() === $(document).height() - $(window).height() && !this.state.isLoading) {
                this.setState({ isLoading: true });
                Actions.getPokemons(this.state.pokemons.length + 1, this.state.pokemons.length + 33);
            }
        }.bind(this));
    },

    onChange: function (msg, pokemons) {
        this.setState({ pokemons: pokemons });
        this.setState({ isLoading: false });
    },

    render: function () {
        document.body.style.background = "#EEE";
        var margin = { marginTop: 30 };
